CREATE FUNCTION [dbo].[FN_DepthLevelBOM]()
returns @retDepthLevelBOM TABLE
(
    BOM nvarchar(35) NOT NULL,
    DepthLevel smallint NOT NULL	
)
AS
BEGIN
WITH 
Components (Component, BOM) AS
(
    SELECT  DISTINCT
		  C.Component, O.BOM
    FROM	  MA_MOComponents C
		  JOIN	MA_MO O
			 ON C.MOId = O.MOId
    WHERE	  C.Component != O.BOM
		  AND C.ReferredPosition = -1
    EXCEPT
    SELECT  DISTINCT
		  O.BOM, C.Component
    FROM	  MA_MOComponents C
		  JOIN	MA_MO O
			 ON C.MOId = O.MOId
    WHERE	  C.Component != O.BOM
		  AND C.ReferredPosition = -1

), 
Lev0 (BOM)
AS
(
	 SELECT	DISTINCT
			BOM
	 FROM	MA_MO
	 EXCEPT
	 SELECT	DISTINCT
			Component
	 FROM	MA_MOComponents
),
BOM_Lev0 (BOM, MaxLevel) AS
(
    SELECT  BOM, 0 AS MaxLevel
    FROM	  Lev0
) ,
LevComp(Comp, BOM, MaxLevel)
AS
	(
	SELECT	 DISTINCT
			 C.Component, l0.BOM, 1
	FROM	  Components C
		  JOIN	 BOM_Lev0 L0
		  ON		 C.BOM = L0.BOM
	UNION ALL
	SELECT	 V.Component, V.BOM, LC.MaxLevel + 1
	FROM	  Components V
		  JOIN	 LevComp LC
		  ON		V.BOM = LC.Comp AND
				LC.MaxLevel = (SELECT MAX(LC.MaxLevel) OVER())
	WHERE	 V.Component != LC.Comp
	)
INSERT @retDepthLevelBOM
SELECT DISTINCT L.BOM AS BOM,
	   L.MaxLevel as DepthLevel
FROM	   LevComp L
RETURN
END;
