    MA_SaleDoc ON MA_SaleDocDetail.SaleDocId = MA_SaleDoc.SaleDocId
    WHERE (MA_SaleDoc.DocumentType = 3407873 OR MA_SaleDoc.DocumentType = 3407879) AND MA_SaleDocDetail.Invoiced = '0' AND MA_SaleDocDetail.NoInvoice = '0'
