	MA_SaleDoc.SaleDocId				AS DocId,
	MA_SaleDoc.InvEntryId 				AS InvEntryId,
	MA_SaleDoc.DocumentDate				AS DocumentDate, 
	'17991231'							AS OriginalDocumentDate, 
