	SELECT -- Correction of Purchase Invoices, price change on other storages
	17									AS DocumentType, 
	4									AS DocumentSubType, 
	MA_PurchaseDoc.DocumentDate			AS DocumentDate, 
	MA_PurchaseDoc.SupplierDocNo		AS ExternalNumber, 
