	MA_PurchaseDoc.PurchaseDocId = MA_PurchaseDocReferences.PurchaseDocId and
	MA_PurchaseDocReferences.DocumentType = 6684676 and
	MA_PurchaseDocReferences.TypeReference = 'InvMultiSt'
