	  WHEN MA_CrossReferences.DerivedDocType = 27066411 -- MANUFACTURING_ORDE
	  THEN (SELECT MONo FROM MA_MO WHERE MA_MO.MOId = MA_CrossReferences.DerivedDocID)
	  WHEN MA_CrossReferences.DerivedDocType = 27066437 -- BOM_POSTING
	  THEN (SELECT DocumentNo FROM MA_BOMPosting WHERE MA_BOMPosting.BOMPostingId = MA_CrossReferences.DerivedDocID)
	  WHEN MA_CrossReferences.DerivedDocType = 27066409 -- INSPECTION_ORDER
	  THEN (SELECT InspectionOrderNo FROM MA_InspectionOrders WHERE MA_InspectionOrders.InspectionOrderId = MA_CrossReferences.DerivedDocID)
	  WHEN MA_CrossReferences.DerivedDocType = 27066410 -- INSPECTION_NOTES
	  THEN (SELECT InspectionNotesNo FROM MA_InspectionNotes WHERE MA_InspectionNotes.InspectionNotesId = MA_CrossReferences.DerivedDocID)
      WHEN MA_CrossReferences.DerivedDocType = 27066413 -- INVENTORY_ENTRY_SCRAP
	  OR MA_CrossReferences.DerivedDocType = 27066414 -- INVENTORY_ENTRY_GOODS_TBR
	  THEN (SELECT DocNo from MA_InventoryEntries WHERE MA_InventoryEntries.EntryId = MA_CrossReferences.DerivedDocID)
	  WHEN MA_CrossReferences.DerivedDocType = 27066402 -- PURCHASEDOC_INVOICE
	  OR MA_CrossReferences.DerivedDocType = 27066406 	-- PURCHASEDOC_INVOICEFORADVANCE
	  OR MA_CrossReferences.DerivedDocType = 27066403 	-- PURCHASEDOC_INVOICE_CORRECTION
	  OR MA_CrossReferences.DerivedDocType = 27066404 	-- PURCHASEDOC_CREDITNOTE
	  OR MA_CrossReferences.DerivedDocType = 27066405 	-- PURCHASEDOC_DEBITNOTE
	  THEN (SELECT SupplierDocNo FROM MA_PurchaseDoc WHERE MA_PurchaseDoc.PurchaseDocId = MA_CrossReferences.DerivedDocID)
	  WHEN MA_CrossReferences.DerivedDocType = 27066381 -- RTS
	  OR MA_CrossReferences.DerivedDocType = 27066382 	-- RFC
	  OR MA_CrossReferences.DerivedDocType = 27066398 	-- SALEDOC_INTERSTORAGE
	  OR MA_CrossReferences.DerivedDocType = 27066385 	-- SALEDOC_ACCINVOICE
	  OR MA_CrossReferences.DerivedDocType = 27066386 	-- SALEDOC_ACCINVOICE_CORRECTION
	  OR MA_CrossReferences.DerivedDocType = 27066391 	-- SALEDOC_RECEIPT
	  OR MA_CrossReferences.DerivedDocType = 27066433 	-- SALEDOC_RECEIPT_FOR_ISM
	  OR MA_CrossReferences.DerivedDocType = 27066399 	-- SALEDOC_PICKING
	  OR MA_CrossReferences.DerivedDocType = 27066392 	-- SALEDOC_RECEIPT_CORRECTION
	  OR MA_CrossReferences.DerivedDocType = 27066393 	-- SALEDOC_NONCOLLECTEDRECEIPT
	  OR MA_CrossReferences.DerivedDocType = 27066397 	-- SALEDOC_PROFORMAINVOICE
	  OR MA_CrossReferences.DerivedDocType = 27066387 	-- SALEDOC_IMMEDIATEINVOICE	
	  OR MA_CrossReferences.DerivedDocType = 27066388 	-- SALEDOC_IMMINVOICE_CORRECTION
	  OR MA_CrossReferences.DerivedDocType = 27066396 	-- SALEDOC_INVOICEFORADVANCE
	  OR MA_CrossReferences.DerivedDocType = 27066389 	-- SALEDOC_CREDITNOTE
	  OR MA_CrossReferences.DerivedDocType = 27066390	-- SALEDOC_DEBITNOTE
	  OR MA_CrossReferences.DerivedDocType = 27066394   -- SALEDOC_PARAGON
	  OR MA_CrossReferences.DerivedDocType = 27066395 	-- PARAGON_CORRECTION
	  THEN (SELECT DocNo FROM MA_SaleDoc WHERE MA_SaleDoc.SaleDocId = MA_CrossReferences.DerivedDocID)
	  WHEN MA_CrossReferences.DerivedDocType = 27066436 -- ENTRIES_SALESPEOPLE
	  THEN (SELECT DocNo FROM MA_CommissionEntries WHERE MA_CommissionEntries.EntryId = MA_CrossReferences.DerivedDocID)
      WHEN MA_CrossReferences.DerivedDocType = 27066373 -- SUPPLIER_QUOTATION
	  THEN (SELECT QuotationNo from MA_SuppQuotas WHERE MA_SuppQuotas.SuppQuotaId = MA_CrossReferences.DerivedDocID)
	  ELSE ''
      END AS DerivedDocNo	

      FROM MA_CrossReferences
